import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Campaign.css';
import Papa from 'papaparse';

const Campaign = () => {
    const [name, setName] = useState('');
    const [csvFile, setCsvFile] = useState(null);
    const [error, setError] = useState({
        name: '',
        csv: '',
    });
    const navigate = useNavigate();

    const handleCsvUpload = (e) => {
        const file = e.target.files[0];
        setCsvFile(file);
        setError((prev) => ({ ...prev, csv: '' })); // Clear previous CSV error

        if (file) {
            const validTypes = ['text/csv', 'application/vnd.ms-excel'];
            if (!validTypes.includes(file.type)) {
                setError((prev) => ({ ...prev, csv: 'Please upload a valid CSV file.' }));
                setCsvFile(null);
                return;
            }

            Papa.parse(file, {
                header: true,
                complete: (results) => {
                    const { data } = results;

                    // Check if both email and name columns exist and are populated
                    const hasEmailColumn = data.some(row => row.email);
                    const hasNameColumn = data.some(row => row.name);

                    if (!hasEmailColumn || !hasNameColumn) {
                        setError((prev) => ({
                            ...prev,
                            csv: 'CSV must contain both email and name fields with values.',
                        }));
                        setCsvFile(null);
                        return;
                    }

                    // Clear previous CSV error if validations pass
                    setError((prev) => ({ ...prev, csv: '' }));
                },
                error: (error) => {
                    setError((prev) => ({ ...prev, csv: 'Error parsing CSV file: ' + error.message }));
                    setCsvFile(null);
                }
            });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        let hasError = false;

        // Validate campaign name
        if (!name) {
            setError((prev) => ({ ...prev, name: 'Campaign name is required.' }));
            hasError = true;
        } else {
            setError((prev) => ({ ...prev, name: '' })); // Clear previous error
        }

        // Validate CSV file
        if (!csvFile) {
            setError((prev) => ({ ...prev, csv: 'CSV file is required.' }));
            hasError = true;
        } else {
            setError((prev) => ({ ...prev, csv: '' })); // Clear previous error
        }

        if (hasError) {
            return; // Stop if there are errors
        }

        const formData = new FormData();
        formData.append('name', name);
        formData.append('csv', csvFile);

        try {
            const response = await axios.post('http://localhost:8000/api/campaign', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            console.log(response.data);
            // Optionally, navigate to another page or show a success message
        } catch (error) {
            console.error(error.response.data);
            setError((prev) => ({ ...prev, csv: 'Failed to create campaign.' })); // Show error message if API call fails
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token'); // Clear the token from local storage
        localStorage.removeItem('user'); // Clear user data if stored
        navigate('/login'); // Redirect to login page
    };

    return (
        <div className="campaign-container">
            {/* Logout Button */}
            <button className="logout-button" onClick={handleLogout}>
                Logout
            </button>
            
            <form className="campaign-form" onSubmit={handleSubmit}>
                <h2>Create Campaign</h2>
             
                <input 
                    type="text" 
                    placeholder="Campaign Name" 
                    value={name} 
                    onChange={(e) => setName(e.target.value)}  
                    className="input-field"
                />
                   <div className="error-message">{error.name}</div> {/* Display campaign name error */}
              
                <input 
                    type="file" 
                    accept=".csv" 
                    onChange={handleCsvUpload}  
                    className="file-input"
                />
                  <div className="error-message">{error.csv}</div> {/* Display CSV error */}
                <button type="submit" className="submit-button">Create Campaign</button>
            </form>
        </div>
    );
};

export default Campaign;
